import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useForm } from 'react-hook-form';
import { ArrowLeft, Save, Calendar, Stethoscope } from 'lucide-react';
import { appointmentAPI, patientAPI, doctorAPI } from '../services/api';
import toast from 'react-hot-toast';

const AppointmentForm = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const isEdit = Boolean(id);

  const [loading, setLoading] = useState(false);
  const [initialLoading, setInitialLoading] = useState(isEdit);
  const [patients, setPatients] = useState([]);
  const [doctors, setDoctors] = useState([]);
  const [availableSlots, setAvailableSlots] = useState([]);
  const [patientSearch, setPatientSearch] = useState('');
  const [doctorSearch, setDoctorSearch] = useState('');
  const [filteredPatients, setFilteredPatients] = useState([]);
  const [filteredDoctors, setFilteredDoctors] = useState([]);
  const [showPatientDropdown, setShowPatientDropdown] = useState(false);
  const [showDoctorDropdown, setShowDoctorDropdown] = useState(false);
  const [selectedDoctor, setSelectedDoctor] = useState(null);

  const doctorInputRef = useRef(null);

  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
    watch,
  } = useForm();

  const watchedDoctor = watch('doctor');
  const watchedDate = watch('appointmentDate');

  useEffect(() => {
    fetchInitialData();
    if (isEdit) {
      fetchAppointment();
    } else {
      const today = new Date();
      const dateStr = today.toISOString().slice(0, 10);
      const timeStr = `${String(today.getHours()).padStart(2, '0')}:${String(today.getMinutes()).padStart(2, '0')}`;
      reset({
        appointmentDate: dateStr,
        appointmentTime: timeStr,
        duration: 15,
        type: 'consultation',
        reason: 'Not Mentioned',
        amount: '',
        paymentOnline: 0,
        paymentOffline: 0,
      });
    }
  }, [id]);

  useEffect(() => {
    if (watchedDoctor && watchedDate) {
      fetchAvailableSlots();
    }
  }, [watchedDoctor, watchedDate]);

  const fetchInitialData = async () => {
    try {
      const [patientsRes, doctorsRes] = await Promise.all([
        patientAPI.getAll({ limit: 1000 }),
        doctorAPI.getAll({ limit: 1000 }),
      ]);
      setPatients(patientsRes.data.data);
      setDoctors(doctorsRes.data.data);
      setFilteredPatients(patientsRes.data.data);
      setFilteredDoctors(doctorsRes.data.data);
    } catch (error) {
      toast.error('Failed to fetch data');
      console.error('Error fetching initial data:', error);
    }
  };

  const fetchAppointment = async () => {
    try {
      setInitialLoading(true);
      const response = await appointmentAPI.getById(id);
      const appointment = response.data.data;

      const patientName = `${appointment.patient.firstName} ${appointment.patient.lastName}`;
      const doctorName = `Dr. ${appointment.doctor.user.firstName} ${appointment.doctor.user.lastName}`;

      setPatientSearch(patientName);
      setDoctorSearch(doctorName);

      reset({
        patient: appointment.patient._id,
        doctor: appointment.doctor._id,
        appointmentDate: appointment.appointmentDate.split('T')[0],
        appointmentTime: appointment.appointmentTime,
        duration: appointment.duration,
        type: appointment.type,
        reason: appointment.reason,
        notes: appointment.notes || '',
        amount: appointment.amount ?? '',
        paymentOnline: appointment.paymentOnline ?? 0,
        paymentOffline: appointment.paymentOffline ?? 0,
      });

      setSelectedDoctor(appointment.doctor);
    } catch (error) {
      toast.error('Failed to fetch appointment details');
      console.error('Error fetching appointment:', error);
    } finally {
      setInitialLoading(false);
    }
  };

  const fetchAvailableSlots = async () => {
    if (!watchedDoctor || !watchedDate) return;
    try {
      const response = await appointmentAPI.getDoctorAvailability(watchedDoctor, watchedDate);
      setAvailableSlots(response.data.data.availableSlots || []);
    } catch (error) {
      console.error('Error fetching available slots:', error);
      setAvailableSlots([]);
    }
  };

  const handlePatientSearch = (e) => {
    const value = e.target.value;
    setPatientSearch(value);
    setShowPatientDropdown(true);
    const filtered = patients.filter((patient) =>
      `${patient.firstName} ${patient.lastName} ${patient.email}`.toLowerCase().includes(value.toLowerCase())
    );
    setFilteredPatients(filtered);
  };

  const handleDoctorSearch = (e) => {
    const value = e.target.value;
    setDoctorSearch(value);
    setShowDoctorDropdown(true);
    const filtered = doctors.filter((doctor) =>
      `${doctor.user.firstName} ${doctor.user.lastName} ${doctor.specialization}`.toLowerCase().includes(value.toLowerCase())
    );
    setFilteredDoctors(filtered);
  };

  const selectPatient = (patient) => {
    setPatientSearch(`${patient.firstName} ${patient.lastName}`);
    reset({ ...watch(), patient: patient._id });
    setShowPatientDropdown(false);
  };

  const selectDoctor = (doctor) => {
    setDoctorSearch(`Dr. ${doctor.user.firstName} ${doctor.user.lastName}`);
    setSelectedDoctor(doctor);

    if (!isEdit) {
      reset({
        ...watch(),
        doctor: doctor._id,
        amount: doctor.consultationFee ?? '',
        paymentOnline: 0,
        paymentOffline: doctor.consultationFee ?? 0,
      });
    } else {
      const existingPaid = {
        paymentOnline: watch('paymentOnline'),
        paymentOffline: watch('paymentOffline'),
      };
      reset({
        ...watch(),
        doctor: doctor._id,
        amount: doctor.consultationFee ?? watch('amount'),
        ...existingPaid,
      });
    }

    if (doctorInputRef.current) doctorInputRef.current.blur();
    setShowDoctorDropdown(false);
  };

  const onSubmit = async (data) => {
    try {
      setLoading(true);

      if (!data.amount || Number(data.amount) <= 0) {
        if (selectedDoctor && typeof selectedDoctor.consultationFee !== 'undefined') {
          data.amount = selectedDoctor.consultationFee;
        }
      }

      if (!isEdit) {
        const online = Number(data.paymentOnline || 0);
        const offline = Number(data.paymentOffline || 0);
        if (online <= 0 && offline <= 0 && data.amount) {
          data.paymentOffline = Number(data.amount);
        }
      }

      data.amount = Number(data.amount || 0);
      data.paymentOnline = Number(data.paymentOnline || 0);
      data.paymentOffline = Number(data.paymentOffline || 0);

      if (isEdit) {
        await appointmentAPI.update(id, data);
        toast.success('Appointment updated successfully');
      } else {
        await appointmentAPI.create(data);
        toast.success('Appointment created successfully');
      }

      navigate('/appointments');
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to save appointment';
      toast.error(message);
      console.error('Error saving appointment:', error);
    } finally {
      setLoading(false);
    }
  };

  if (initialLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-4xl mx-auto space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center">
          <button onClick={() => navigate('/appointments')} className="mr-4 p-2 text-gray-400 hover:text-gray-600">
            <ArrowLeft className="h-5 w-5" />
          </button>
          <div>
            <h1 className="text-2xl font-bold text-gray-900">{isEdit ? 'Edit Appointment' : 'Book New Appointment'}</h1>
            <p className="text-gray-600">{isEdit ? 'Update appointment details' : 'Schedule a new appointment'}</p>
          </div>
        </div>
        <button onClick={handleSubmit(onSubmit)} disabled={loading} className="btn-primary flex items-center">
          <Save className="h-5 w-5 mr-2" />
          {loading ? 'Saving...' : 'Save Appointment'}
        </button>
      </div>

      {/* Form */}
      <form onSubmit={handleSubmit(onSubmit)} className="space-y-6">
        {/* Appointment Details */}
        <div className="card">
          <div className="flex items-center mb-4">
            <Calendar className="h-5 w-5 text-primary-600 mr-2" />
            <h2 className="text-lg font-medium text-gray-900">Appointment Details</h2>
          </div>

          {/* --- rest of your form unchanged --- */}
          {/* (Patient, Doctor, Date, Time, Reason, etc.) */}
        </div>

        {/* Payment Details */}
        <div className="card">
          <div className="flex items-center mb-4">
            <Stethoscope className="h-5 w-5 text-primary-600 mr-2" />
            <h2 className="text-lg font-medium text-gray-900">Payment</h2>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            <div className="form-group">
              <label className="form-label">Consultation Fee</label>
              <div className="input-field bg-gray-50">
                {typeof selectedDoctor?.consultationFee !== 'undefined'
                  ? selectedDoctor.consultationFee
                  : watch('amount') || '-'}
              </div>
              <input type="hidden" {...register('amount')} />
            </div>
            <div className="form-group">
              <label className="form-label">Paid Online</label>
              <input type="number" step="0.01" {...register('paymentOnline')} className="input-field" placeholder="0" />
            </div>
            <div className="form-group">
              <label className="form-label">Paid Offline</label>
              <input type="number" step="0.01" {...register('paymentOffline')} className="input-field" placeholder="0" />
            </div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default AppointmentForm;
