import React, { useRef, useState } from 'react';
import { dispensaryAPI } from '../services/api';
import toast from 'react-hot-toast';

const emptyItem = { name: '', quantity: 1, unitPrice: 0 };

const Dispensary = () => {
  const [mode, setMode] = useState('token'); // 'token' | 'patient'
  const [date, setDate] = useState('');
  const [token, setToken] = useState('');
  const [patientId, setPatientId] = useState('');
  const [items, setItems] = useState([{ ...emptyItem }]);
  const [tax, setTax] = useState('0');
  const [loading, setLoading] = useState(false);

  const [queryDate, setQueryDate] = useState('');
  const [queryToken, setQueryToken] = useState('');
  const [queryPatientId, setQueryPatientId] = useState('');
  const [results, setResults] = useState([]);
  const [selected, setSelected] = useState(null);
  const [payAmount, setPayAmount] = useState('');
  const billRef = useRef(null);
  const [queryLoading, setQueryLoading] = useState(false);

  const updateItem = (idx, field, value) => {
    setItems(prev => prev.map((it, i) => i === idx ? { ...it, [field]: value } : it));
  };

  const addItem = () => setItems(prev => [...prev, { ...emptyItem }]);
  const removeItem = (idx) => setItems(prev => prev.filter((_, i) => i !== idx));

  const computeSubtotal = () => items.reduce((sum, it) => sum + (Number(it.quantity || 0) * Number(it.unitPrice || 0)), 0);
  const subtotal = computeSubtotal();
  const total = subtotal + Number(tax || 0);

  const onCreate = async () => {
    try {
      setLoading(true);
      const payload = { items, tax: Number(tax || 0) };
      if (mode === 'token') {
        if (!date || !token) return toast.error('Provide date and token');
        payload.date = date;
        payload.token = Number(token);
      } else {
        if (!patientId) return toast.error('Provide patientId');
        payload.patient = patientId;
      }
      const res = await dispensaryAPI.create(payload);
      toast.success('Dispense created');
      // Reset minimal
      setItems([{ ...emptyItem }]);
      setTax('0');
    } catch (e) {
      const msg = e.response?.data?.message || 'Failed to create dispense';
      toast.error(msg);
      console.error('Create dispense error:', e);
    } finally {
      setLoading(false);
    }
  };

  const onSelect = (d) => {
    setSelected(d);
    setPayAmount('');
  };

  const onPay = async () => {
    if (!selected?._id) return toast.error('Select a dispense first');
    if (!payAmount || Number(payAmount) <= 0) return toast.error('Enter valid amount');
    try {
      const res = await dispensaryAPI.pay(selected._id, { amount: Number(payAmount) });
      toast.success('Payment recorded');
      setSelected(res.data.data);
      // refresh list entry
      setResults(prev => prev.map(x => x._id === res.data.data._id ? res.data.data : x));
      setPayAmount('');
    } catch (e) {
      const msg = e.response?.data?.message || 'Payment failed';
      toast.error(msg);
      console.error('Payment error:', e);
    }
  };

  const onPrint = () => {
    if (!billRef.current) return;
    const printContents = billRef.current.innerHTML;
    const win = window.open('', '', 'width=800,height=600');
    win.document.write('<html><head><title>Bill</title>');
    win.document.write('<style>body{font-family:Arial;padding:16px} table{width:100%;border-collapse:collapse} th,td{border:1px solid #ddd;padding:8px;text-align:left}</style>');
    win.document.write('</head><body>');
    win.document.write(printContents);
    win.document.write('</body></html>');
    win.document.close();
    win.focus();
    win.print();
    win.close();
  };

  const onQuery = async () => {
    try {
      setQueryLoading(true);
      const params = {};
      if (queryPatientId) params.patientId = queryPatientId;
      if (queryDate && queryToken) {
        params.date = queryDate;
        params.token = queryToken;
      }
      if (!params.patientId && !params.date) return toast.error('Provide patientId or date+token');
      const res = await dispensaryAPI.getAll(params);
      setResults(res.data.data || []);
      setSelected(null);
    } catch (e) {
      const msg = e.response?.data?.message || 'Failed to fetch dispenses';
      toast.error(msg);
      console.error('Query dispenses error:', e);
    } finally {
      setQueryLoading(false);
    }
  };

  return (
    <div className="space-y-6">
      <div className="card">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Create Dispense</h2>
        <div className="flex items-center gap-4 mb-4">
          <label className="flex items-center gap-2">
            <input type="radio" name="mode" checked={mode==='token'} onChange={() => setMode('token')} /> By Token
          </label>
          <label className="flex items-center gap-2">
            <input type="radio" name="mode" checked={mode==='patient'} onChange={() => setMode('patient')} /> By Patient ID
          </label>
        </div>
        {mode === 'token' ? (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
            <div>
              <label className="form-label">Date</label>
              <input type="date" className="input-field" value={date} onChange={(e)=>setDate(e.target.value)} />
            </div>
            <div>
              <label className="form-label">Token</label>
              <input type="number" className="input-field" value={token} onChange={(e)=>setToken(e.target.value)} />
            </div>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
            <div>
              <label className="form-label">Patient ID</label>
              <input type="text" className="input-field" value={patientId} onChange={(e)=>setPatientId(e.target.value)} placeholder="Mongo ObjectId" />
            </div>
          </div>
        )}

        <div className="mb-4">
          <label className="form-label">Items</label>
          <div className="space-y-2">
            {items.map((it, idx) => (
              <div key={idx} className="grid grid-cols-1 md:grid-cols-12 gap-2">
                <input className="input-field md:col-span-6" placeholder="Medicine name" value={it.name} onChange={(e)=>updateItem(idx,'name',e.target.value)} />
                <input type="number" className="input-field md:col-span-2" placeholder="Qty" value={it.quantity} onChange={(e)=>updateItem(idx,'quantity',e.target.value)} />
                <input type="number" className="input-field md:col-span-2" placeholder="Unit Price" value={it.unitPrice} onChange={(e)=>updateItem(idx,'unitPrice',e.target.value)} />
                <button type="button" className="btn-secondary md:col-span-2" onClick={()=>removeItem(idx)}>Remove</button>
              </div>
            ))}
          </div>
          <div className="mt-2">
            <button type="button" className="btn-secondary" onClick={addItem}>Add Item</button>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
          <div>
            <label className="form-label">Tax</label>
            <input type="number" className="input-field" value={tax} onChange={(e)=>setTax(e.target.value)} />
          </div>
          <div className="md:col-span-2 flex items-end justify-end">
            <div className="text-right">
              <div className="text-sm text-gray-600">Subtotal: {subtotal.toFixed(2)}</div>
              <div className="text-sm text-gray-600">Tax: {Number(tax||0).toFixed(2)}</div>
              <div className="text-lg font-semibold">Total: {total.toFixed(2)}</div>
            </div>
          </div>
        </div>

        <button className="btn-primary" onClick={onCreate} disabled={loading}>
          {loading ? 'Saving...' : 'Create Dispense'}
        </button>
      </div>

      <div className="card">
        <h2 className="text-lg font-semibold text-gray-900 mb-4">Find Dispenses</h2>
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div>
            <label className="form-label">Patient ID</label>
            <input type="text" className="input-field" value={queryPatientId} onChange={(e)=>setQueryPatientId(e.target.value)} />
          </div>
          <div>
            <label className="form-label">Date</label>
            <input type="date" className="input-field" value={queryDate} onChange={(e)=>setQueryDate(e.target.value)} />
          </div>
          <div>
            <label className="form-label">Token</label>
            <input type="number" className="input-field" value={queryToken} onChange={(e)=>setQueryToken(e.target.value)} />
          </div>
          <div className="flex items-end">
            <button className="btn-primary" onClick={onQuery} disabled={queryLoading}>{queryLoading ? 'Searching...' : 'Search'}</button>
          </div>
        </div>

        <div className="mt-4 overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Patient</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Token</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Subtotal</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Tax</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Total</th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                <th className="px-6 py-3"></th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {results.map(d => (
                <tr key={d._id} className={selected?._id===d._id ? 'bg-primary-50' : ''}>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{d.patient?.firstName} {d.patient?.lastName}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{d.dailyToken ?? '-'}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{Number(d.subtotal).toFixed(2)}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{Number(d.tax).toFixed(2)}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{Number(d.total).toFixed(2)}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">{d.paymentStatus}</td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-right">
                    <button className="btn-secondary" onClick={()=>onSelect(d)}>Select</button>
                  </td>
                </tr>
              ))}
              {results.length === 0 && (
                <tr>
                  <td colSpan="6" className="px-6 py-6 text-center text-gray-500">No dispenses</td>
                </tr>
              )}
            </tbody>
          </table>
        </div>
      </div>

      {/* Payment and Bill */}
      {selected && (
        <div className="card">
          <div className="flex items-center justify-between mb-4">
            <h2 className="text-lg font-semibold text-gray-900">Payment & Bill</h2>
            <button className="btn-secondary" onClick={()=>setSelected(null)}>Clear</button>
          </div>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-4">
            <div>
              <label className="form-label">Amount to pay</label>
              <input type="number" className="input-field" value={payAmount} onChange={(e)=>setPayAmount(e.target.value)} />
            </div>
            <div className="flex items-end">
              <button className="btn-primary" onClick={onPay}>Record Payment</button>
            </div>
            <div className="flex items-end">
              <button className="btn-secondary" onClick={onPrint} disabled={!selected.billNumber && selected.paymentStatus!=='paid'}>Print Bill</button>
            </div>
          </div>

          <div ref={billRef} className="bg-white p-4 rounded border">
            <h3 className="text-xl font-semibold mb-2">Clinic Bill</h3>
            <div className="text-sm text-gray-600 mb-4">Bill No: {selected.billNumber || '(generated after payment)'} | Date: {new Date(selected.createdAt).toLocaleString()}</div>
            <div className="mb-2"><strong>Patient:</strong> {selected.patient?.firstName} {selected.patient?.lastName}</div>
            <div className="mb-4"><strong>Token:</strong> {selected.dailyToken ?? '-'} | <strong>Day:</strong> {selected.appointmentDay ? new Date(selected.appointmentDay).toLocaleDateString() : '-'}</div>
            <table>
              <thead>
                <tr><th>Item</th><th>Qty</th><th>Unit Price</th><th>Amount</th></tr>
              </thead>
              <tbody>
                {selected.items?.map((it, idx)=> (
                  <tr key={idx}><td>{it.name}</td><td>{it.quantity}</td><td>{Number(it.unitPrice).toFixed(2)}</td><td>{(Number(it.quantity)*Number(it.unitPrice)).toFixed(2)}</td></tr>
                ))}
              </tbody>
            </table>
            <div className="text-right mt-4">
              <div>Subtotal: {Number(selected.subtotal).toFixed(2)}</div>
              <div>Tax: {Number(selected.tax).toFixed(2)}</div>
              <div className="font-semibold">Total: {Number(selected.total).toFixed(2)}</div>
              <div>Paid: {Number(selected.paidAmount||0).toFixed(2)}</div>
              <div>Status: {selected.paymentStatus}</div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dispensary;
