import React, { useState, useRef } from 'react';
import { useForm } from 'react-hook-form';

const AppointmentForm = ({ doctors, patients, isEdit, onSubmit }) => {
  const {
    register,
    handleSubmit,
    reset,
    watch,
  } = useForm();

  const [doctorSearch, setDoctorSearch] = useState('');
  const [showDoctorDropdown, setShowDoctorDropdown] = useState(false);
  const [selectedDoctor, setSelectedDoctor] = useState(null);
  const doctorInputRef = useRef(null);

  const [patientSearch, setPatientSearch] = useState('');
  const [showPatientDropdown, setShowPatientDropdown] = useState(false);
  const [selectedPatient, setSelectedPatient] = useState(null);
  const patientInputRef = useRef(null);

  // -------------------------
  // Select Patient
  // -------------------------
  const selectPatient = (patient) => {
    setPatientSearch(`${patient.firstName} ${patient.lastName}`);
    setSelectedPatient(patient);

    reset({
      ...watch(),
      patient: patient._id,
    });

    if (patientInputRef.current) patientInputRef.current.blur();
    setShowPatientDropdown(false);
  };

  // -------------------------
  // Select Doctor
  // -------------------------
  const selectDoctor = (doctor) => {
    setDoctorSearch(`Dr. ${doctor.user.firstName} ${doctor.user.lastName}`);
    setSelectedDoctor(doctor);

    if (!isEdit) {
      // For new appointment
      reset({
        ...watch(),
        doctor: doctor._id,
        amount: doctor.consultationFee ?? '',
        paymentOnline: 0,
        paymentOffline: doctor.consultationFee ?? 0,
      });
    } else {
      // For editing existing appointment
      const existingPaid = {
        paymentOnline: watch('paymentOnline'),
        paymentOffline: watch('paymentOffline'),
      };

      reset({
        ...watch(),
        doctor: doctor._id,
        amount: doctor.consultationFee ?? watch('amount'),
        ...existingPaid,
      });
    }

    if (doctorInputRef.current) doctorInputRef.current.blur();
    setShowDoctorDropdown(false);
  };

  // -------------------------
  // Handle Form Submit
  // -------------------------
  const handleFormSubmit = (data) => {
    onSubmit(data);
    reset();
    setSelectedDoctor(null);
    setSelectedPatient(null);
    setDoctorSearch('');
    setPatientSearch('');
  };

  return (
    <form
      onSubmit={handleSubmit(handleFormSubmit)}
      className="max-w-2xl mx-auto bg-white shadow-md rounded-2xl p-6 space-y-4"
    >
      {/* Patient Selection */}
      <div>
        <label className="block font-medium text-gray-700 mb-1">Patient</label>
        <input
          type="text"
          value={patientSearch}
          onChange={(e) => {
            setPatientSearch(e.target.value);
            setShowPatientDropdown(true);
          }}
          onFocus={() => setShowPatientDropdown(true)}
          ref={patientInputRef}
          placeholder="Search patient..."
          className="w-full border rounded-xl px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />

        {showPatientDropdown && (
          <ul className="border mt-1 rounded-xl max-h-48 overflow-y-auto bg-white shadow">
            {patients
              .filter((p) =>
                `${p.firstName} ${p.lastName}`
                  .toLowerCase()
                  .includes(patientSearch.toLowerCase())
              )
              .map((p) => (
                <li
                  key={p._id}
                  onClick={() => selectPatient(p)}
                  className="px-3 py-2 hover:bg-blue-100 cursor-pointer"
                >
                  {p.firstName} {p.lastName}
                </li>
              ))}
          </ul>
        )}
      </div>

      {/* Doctor Selection */}
      <div>
        <label className="block font-medium text-gray-700 mb-1">Doctor</label>
        <input
          type="text"
          value={doctorSearch}
          onChange={(e) => {
            setDoctorSearch(e.target.value);
            setShowDoctorDropdown(true);
          }}
          onFocus={() => setShowDoctorDropdown(true)}
          ref={doctorInputRef}
          placeholder="Search doctor..."
          className="w-full border rounded-xl px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />

        {showDoctorDropdown && (
          <ul className="border mt-1 rounded-xl max-h-48 overflow-y-auto bg-white shadow">
            {doctors
              .filter((d) =>
                `${d.user.firstName} ${d.user.lastName}`
                  .toLowerCase()
                  .includes(doctorSearch.toLowerCase())
              )
              .map((d) => (
                <li
                  key={d._id}
                  onClick={() => selectDoctor(d)}
                  className="px-3 py-2 hover:bg-blue-100 cursor-pointer"
                >
                  Dr. {d.user.firstName} {d.user.lastName} — Fee: ₹{d.consultationFee ?? 0}
                </li>
              ))}
          </ul>
        )}
      </div>

      {/* Amount */}
      <div>
        <label className="block font-medium text-gray-700 mb-1">Amount (₹)</label>
        <input
          type="number"
          {...register('amount', { required: true })}
          className="w-full border rounded-xl px-3 py-2 focus:outline-none focus:ring-2 focus:ring-blue-500"
        />
      </div>

      {/* Payment */}
      <div className="grid grid-cols-2 gap-4">
        <div>
          <label className="block font-medium text-gray-700 mb-1">Online Payment (₹)</label>
          <input
            type="number"
            {...register('paymentOnline')}
            className="w-full border rounded-xl px-3 py-2"
          />
        </div>
        <div>
          <label className="block font-medium text-gray-700 mb-1">Offline Payment (₹)</label>
          <input
            type="number"
            {...register('paymentOffline')}
            className="w-full border rounded-xl px-3 py-2"
          />
        </div>
      </div>

      {/* Submit */}
      <div className="pt-4">
        <button
          type="submit"
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-medium py-2 rounded-xl transition"
        >
          {isEdit ? 'Update Appointment' : 'Create Appointment'}
        </button>
      </div>
    </form>
  );
};

export default AppointmentForm;
